from matplotlib.pyplot import *

def plotFeatureSelectionGraph(_title, numFeaturesList, name1, data1, name2, data2, name3, data3,numFeatures=totNumFeatures):
    xAxis = toPrecents(numFeaturesList, numFeatures)
#    xAxis = numFeaturesList
    l1=plot(xAxis,data1,'blue',label=name1)
    l2=plot(xAxis,data2,'red',label=name2)
    l3=plot(xAxis,data3,'black',label=name3)
    xlabel('%features_selected')
    ylabel('accuracy')
    legend()
    title(_title)
    show()
    
def toPrecents(numFeaturesList, totFeatures):
    res = numFeaturesList[:]
    res = map(lambda x: round(100 * float(x)/totFeatures),res)
    return res

plotFeatureSelectionGraph('information gain feature selectopn', info_gain_numFeaturesAxis, 'naive bayes', info_gain_naive_bayes, 'linear svm', info_gain_linear_svm, 'hyperbolic svm', info_gain_hyperbolic_svm, totNumFeatures)