import numpy
from matplotlib.mlab import normpdf

def createGraph(list1,list2=None,list3=None,list4=None,_title='',firstTitle='random_reordering',secondTitle='spins_first',thirdTitle='moves_first',fourthTitle='interleve_reordering', fifthTitle = 'no_reordering'):
    n, bins, patches = hist([list1,list2,list3,list4],bins=10,
    color=['crimson', 'orange', 'chartreuse','blue'], label=[firstTitle + " (" + str(average(list1))[0:5] + ")", secondTitle + " (" + str(average(list2))[0:5] + ")", thirdTitle + " (" + str(average(list3))[0:5] + ")",fourthTitle + " (" + str(average(list4))[0:5] + ")"])
    legend()
    xlabel('%correct')
    ylabel('number of results')
    title(_title)
    show()

